'use strict'

const BaseMob = require('../BaseMob.js')

class Datastore extends BaseMob {

	async createDirectory(name) {
		let svc = await this._service()
		let datastoreNamespaceManager = svc.serviceContent.datastoreNamespaceManager
		return svc.vimPort.createDirectory(datastoreNamespaceManager, this.mor, name)
	}

	async deleteDirectory(datacenterMor, datastorePath) {
		let svc = await this._service()
		let datastoreNamespaceManager = svc.serviceContent.datastoreNamespaceManager
		return svc.vimPort.deleteDirectory(datastoreNamespaceManager, datacenterMor, datastorePath)
	}
}


module.exports = Datastore